import os
import requests
import json
from langflow.custom.custom_component.component import Component
from langflow.inputs.inputs import MessageTextInput
from langflow.io import Output
from langflow.schema.data import Data

class ImageGenerationComponent(Component):
    display_name = "Image Generation"
    description = "Generate images using nano-banana-2 model."
    icon = "image"

    inputs = [
        MessageTextInput(
            name="prompt",
            display_name="Prompt",
            info="The image description.",
            tool_mode=True,
            required=True
        ),
        MessageTextInput(
            name="aspect_ratio",
            display_name="Aspect Ratio",
            info="e.g., 1:1, 16:9",
            tool_mode=True
        ),
        MessageTextInput(
            name="image_size",
            display_name="Image Size",
            info="e.g., 1024x1024",
            tool_mode=True
        ),
    ]

    outputs = [
        Output(display_name="Data", name="result", type_=Data, method="generate_image"),
    ]

    def generate_image(self) -> Data:
        api_key = os.getenv("OPENAI_API_KEY")
        base_url = os.getenv("OPENAI_BASE_URL", "https://api.openai.com")

        if not api_key:
            return Data(data={"error": "Missing OPENAI_API_KEY"})

        url = f"{base_url}/v1/images/generations"
        headers = {
            "Authorization": f"Bearer {api_key}",
            "Content-Type": "application/json"
        }

        payload = {
            "model": "nano-banana-2",
            "prompt": self.prompt,
            "response_format": "url"
        }

        if self.aspect_ratio:
            payload["aspect_ratio"] = self.aspect_ratio
        if self.image_size:
            payload["image_size"] = self.image_size

        try:
            response = requests.post(url, headers=headers, json=payload)
            # handle non-200 generally
            if response.status_code != 200:
                 return Data(data={"error": f"Error {response.status_code}: {response.text}"})

            data = response.json()
            # Extract images safely
            images = [item.get("url") for item in data.get("data", [])]
            return Data(data={"images": images, "raw": data})

        except Exception as e:
            return Data(data={"error": str(e)})

    def build(self):
        return self.generate_image
